import Foundation


func convertUnicode(input:String) -> String{
    let list = input.split{$0 == " "}.map(String.init)
    var emoji: String = ""
    var unicodeStr: String = ""
    for each in list{
        let zeroStr = String(repeating: "0", count: (7 - each.count))
        unicodeStr = zeroStr + each
        let unicodeInt = Int(unicodeStr, radix: 16)
        let unicodeOutput: String = String(UnicodeScalar(unicodeInt!)!)
        emoji += unicodeOutput
    }
    return emoji
}

